module.exports = OutputDocument;

function OutputDocument(document) {
  if (!(this instanceof OutputDocument))
    return new OutputDocument(document);

    this.document_ = document;
}

OutputDocument.prototype.output = function(text) {
    // document.write(text + "<br>");
    var outputArea = this.document_.getElementById("output");
    var txt = this.document_.createTextNode(text);
    var br = this.document_.createElement("br");
    outputArea.appendChild(txt);
    outputArea.appendChild(br);
}

OutputDocument.prototype.clearOutput = function() {
  var myNode = this.document_.getElementById("output");
  while (myNode.firstChild) {
      myNode.removeChild(myNode.firstChild);
  }
}
